import React, {useEffect} from 'react';
import {useParams} from "react-router";
import {useDispatch, useSelector} from "react-redux";
import action from './../../redux/usersState/actions'
import {Avatar, Container, LinkToHub} from "../home";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;

`

const ContainerUser = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
  border: 2px solid gray;
  border-radius: 10px;
`

const Name = styled.div`
  font-size: 36px;
  font-weight: bold;
`

const BioWrapper = styled.div`
  margin-top: 20px;
`


const User = () => {
    const { userName } = useParams();
    const dispatch = useDispatch();
    const user = useSelector(state => state.users.currentUser);

    useEffect(() => {
        dispatch(action.getUser(userName))
    }, []);

    return (
        <Container>
            <ContainerUser>
                {
                    user &&
                   ( <div>
                       <Wrapper>
                       <Avatar size={300} src={`${user.avatar_url}`} alt=""/>
                       <div>
                            <Name>{user.name}</Name>
                            <p>Followers: {user.followers ?? '-'}</p>
                            <p>Following: {user.following ?? '-'}</p>
                            <p>Created at: {user.created_at ?? '-'}</p>
                            <p>Company: {user.company ?? '-'}</p>
                            <p>Email: {user.email ?? '-'}</p>
                            <p>Location: {user.location ?? '-'}</p>
                            <p>Blog: <LinkToHub href={user.blog}>{user.blog ?? '-'}</LinkToHub></p>

                       </div>
                       </Wrapper>
                       <BioWrapper>
                           <p>Bio </p>
                           <p>{user.bio ?? '-'}</p>
                       </BioWrapper>

                </div>)
                }
            </ContainerUser>
        </Container>
    );
};

export default User;
