import React, {useEffect} from 'react';
import {useParams} from "react-router";
import {useDispatch, useSelector} from "react-redux";
import action from './../../redux/usersState/actions'
import {Avatar, Container, LinkToHub} from "../home";
import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;

`

const ContainerUser = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
  border: 2px solid gray;
  border-radius: 10px;
`

const Name = styled.div`
  font-size: 36px;
  font-weight: bold;
`

const BioWrapper = styled.div`
  margin-top: 20px;
`

const BioTitle = styled.div`
  font-size: 24px;
  font-weight: bold;
`


const User = () => {
    const { userName } = useParams();
    const dispatch = useDispatch();
    const user = useSelector(state => state.users.currentUser);

    useEffect(() => {
        dispatch(action.getUser(userName))
    }, []);

    const formatData = (date) =>{
        return new Date(date).toLocaleDateString()
    }

    const renderDataOrNothing = (data) =>{
        return data ? data : '-'
    }

    return (
        <Container>
            <ContainerUser>
                {
                    user &&
                   ( <div>
                       <Wrapper>
                       <Avatar size={300} src={`${user.avatar_url}`} alt=""/>
                       <div>
                            <Name>{user.name}</Name>
                            <p>Followers: {renderDataOrNothing(user.followers)}</p>
                            <p>Following: {renderDataOrNothing(user.following)}</p>
                            <p>Created at: {renderDataOrNothing(formatData(user.created_at))}</p>
                            <p>Company: {renderDataOrNothing(user.company)}</p>
                            <p>Email: {renderDataOrNothing(user.email)}</p>
                            <p>Location: {renderDataOrNothing(user.location)}</p>
                            <p>Blog: <LinkToHub href={user.blog}>{renderDataOrNothing(user.blog)}</LinkToHub></p>
                       </div>
                       </Wrapper>
                       <BioWrapper>
                           <BioTitle>Bio </BioTitle>
                           <p>{renderDataOrNothing(user.bio)}</p>
                       </BioWrapper>

                </div>)
                }
            </ContainerUser>
        </Container>
    );
};

export default User;
