import React from 'react';
import {ItemData} from "../../pages/user";
import styled from "styled-components";
import {Link} from "react-router-dom";
import {LinkToHub} from "../../pages/home";

const ItemWrapper = styled(Link)`
  width: 100%;
  border-radius: 10px;
  border: 1px solid grey;
  display: flex;
  margin-top: 20px;
  margin-bottom: 20px;
  text-decoration: none;
  color: black;
  padding: 10px;
`

export const Avatar = styled.img`
  width: ${props => props.size | 100}px;
  height: ${props => props.size | 100}px;
  border-radius: 50%;
  margin-right: 30px;
`

const ItemRightPart = styled.div`
  display: flex;
  flex-direction: column;
`


const Item = ({user}) => {
    return (
        <ItemWrapper to={`/${user.login}`} key={user.id}>
            <Avatar src={`${user.avatar_url}`} alt="avatar"/>
            <ItemRightPart>
                <ItemData>Login: {user.login}</ItemData>
                <ItemData>Link to hub: <LinkToHub href={user.html_url}>{user.html_url}</LinkToHub></ItemData>
            </ItemRightPart>
        </ItemWrapper>
    );
};

export default Item;
