import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {usePagination} from "../../utils/hooks/usePagination";
import action from './../../redux/usersState/actions'
import styled from 'styled-components'
import {Link} from "react-router-dom";
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

const ContainerItems = styled.div`
  display: flex;
  flex-direction: column;
  width: 400px;
`


const Item = styled(Link)`
  width: 100%;
  border-radius: 10px;
  border: 1px solid red;
  display: flex;
  margin-top: 20px;
  margin-bottom: 20px;
  text-decoration: none;
  color: black;
  padding: 10px;
`

const Avatar = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  margin-right: 30px;
`

const ItemRightPart = styled.div`
  display: flex;
  flex-direction: column;
`

const Login = styled.p`
`

const LinkToHub = styled.a`
    text-decoration: none;
    color: black;
      &:hover {
        color: red;
      }
`

const LoadingContainer = styled.div`
  position:relative;
  display:flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-evenly;
  width:100%
`

const Loading = styled.div`
    display: inline-block;
    position: relative;
    width: 80px;
    height: 80px;
   div {
    position: absolute;
    border: 4px solid #fff;
    opacity: 1;
    border-radius: 50%;
    animation: a 1s cubic-bezier(0, 0.2, 0.8, 1) infinite;
  }
   div:nth-child(2) {
    animation-delay: -0.5s;
  }
  @keyframes a {
    0% {
      top: 36px;
      left: 36px;
      width: 0;
      height: 0;
      opacity: 0;
      background-color:#203060
    }
    4.9% {
      top: 36px;
      left: 36px;
      width: 0;
      height: 0;
      opacity: 0;
    }
    5% {
      top: 36px;
      left: 36px;
      width: 0;
      height: 0;
      opacity: 1;
    }
    100% {
      top: 0px;
      left: 0px;
      width: 72px;
      height: 72px;
      opacity: 0;
    }
  }
`

const LoadMoreButton = styled.button`
  border: 1px solid red;
  border-radius: 20px;
  font-size: 24px;
  padding: 10px 80px;
  background-color: transparent;
  cursor: pointer;
`

const Home = () => {
    const dispatch = useDispatch();
    const users = useSelector(state => state.users.users);
    const loading = useSelector(state => state.users.loading);
    const pagination = usePagination();

    useEffect(() => {
        dispatch(action.getAllUsers(pagination.take, pagination.skip));
    },[pagination.take, pagination.skip]);

    const onAddMoreUsers = () => {
        pagination.setPage(pagination.page + 1);
    }

    return (
        <Container>
            users from github
            <ContainerItems>
            {
                users.map(x => {
                    return <Item to={`/${x.id}`} key={x.id}>
                        <Avatar src={`${x.avatar_url}`} alt=""/>
                        <ItemRightPart>
                        <Login>Login: {x.login}</Login>
                        <LinkToHub href={x.html_url}>Link to hub</LinkToHub>
                        </ItemRightPart>
                    </Item>
                })
            }
            <LoadMoreButton onClick={onAddMoreUsers}>
                Load more
            </LoadMoreButton>
            {loading &&
                <LoadingContainer>
                    <Loading className="a">
                        <div/>
                        <div/>
                    </Loading>
                </LoadingContainer>
            }
            </ContainerItems>
        </Container>
    );
};

export default Home;
