import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {usePagination} from "../../utils/hooks/usePagination";
import action from './../../redux/usersState/actions'
import styled from 'styled-components'
const Container = styled.div`
    display: flex;
 flex-direction: column;
  align-items: center;
`

const Item = styled.div`
  border: 1px solid red;
    display: flex;
  margin-top: 20px;
  margin-bottom: 20px;
`

const Avatar = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 50%;
`

const Hoome = () => {
    const dispatch = useDispatch();
    const users = useSelector(state => state.users.users);
    const pagination = usePagination();

    useEffect(() => {
        dispatch(action.getAllUsers(pagination.take, pagination.skip));
    },[pagination.take, pagination.skip]);

    const aadd = () => {
        pagination.setPage(pagination.page + 1);
    }

    return (
        <Container>
            {
                users.map(x => {
                    return <Item key={x.id}>
                        <Avatar src={`${x.avatar_url}`} alt=""/>
                         <p>Login: {x.login}</p>
                        <a href={x.html_url}>Link to hub</a>
                    </Item>
                })
            }
        </Container>
    );
};

export default Hoome;
