import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {usePagination} from "../../utils/hooks/usePagination";
import action from './../../redux/usersState/actions'
import styled from 'styled-components'
import {Link} from "react-router-dom";
import Loading from "../../components/loading/loading";
import {ItemData, Title} from "../user";

export const Container = styled.div`
  margin: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const ContainerItems = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 400px;
`


const Item = styled(Link)`
  width: 100%;
  border-radius: 10px;
  border: 1px solid grey;
  display: flex;
  margin-top: 20px;
  margin-bottom: 20px;
  text-decoration: none;
  color: black;
  padding: 10px;
`

export const Avatar = styled.img`
  width: ${props => props.size | 100}px;
  height: ${props => props.size | 100}px;
  border-radius: 50%;
  margin-right: 30px;
`

const ItemRightPart = styled.div`
  display: flex;
  flex-direction: column;
`

const Login = styled.p`
`

export const LinkToHub = styled.a`
    text-decoration: none;
    color: black;
      &:hover {
        color: grey;
      }
`

const LoadMoreButton = styled.button`
  border: 1px solid grey;
  border-radius: 20px;
  font-size: 24px;
  padding: 10px 80px;
  background-color: transparent;
  cursor: pointer;
`

const Home = () => {
    const dispatch = useDispatch();
    const users = useSelector(state => state.users.users);
    const loading = useSelector(state => state.users.loading);
    const pagination = usePagination();

    useEffect(() => {
        dispatch(action.getAllUsers(pagination.take, pagination.skip));
    },[pagination.take, pagination.skip]);

    const onAddMoreUsers = () => {
        pagination.setPage(pagination.page + 1);
    }

    return (
        <Container>
            <Title>Users from github</Title>
            <ContainerItems>
            {
                users.map(user => <Item user={user}/>)
            }
                {!loading && <LoadMoreButton onClick={onAddMoreUsers}>
                    Load more
                </LoadMoreButton>}
            {loading && <Loading/>}
            </ContainerItems>
        </Container>
    );
};

export default Home;
